// App.js - Main React Component for NFL Query Interface
import React, { useState, useEffect } from 'react';
import './App.css';

// API Configuration
const API_BASE_URL = 'http://localhost:8000';

// Utility function for API calls
const apiCall = async (endpoint, options = {}) => {
  const url = `${API_BASE_URL}${endpoint}`;
  const config = {
    headers: {
      'Content-Type': 'application/json',
    },
    ...options,
  };

  const response = await fetch(url, config);
  if (!response.ok) {
    throw new Error(`API Error: ${response.statusText}`);
  }
  return response.json();
};

// Database Status Component
const DatabaseStatus = ({ status, onRefresh }) => {
  return (
    <div className={`status-card ${status.connected ? 'connected' : 'disconnected'}`}>
      <div className="status-header">
        <h3>üìä Database Status</h3>
        <button onClick={onRefresh} className="refresh-btn">üîÑ</button>
      </div>

      {status.connected ? (
        <div className="status-details">
          <div className="status-indicator">
            <span className="indicator green"></span>
            <span>Connected</span>
          </div>
          <div className="metric">
            <strong>{status.total_plays?.toLocaleString()}</strong> total plays
          </div>
          <div className="qb-list">
            <strong>Available QBs:</strong>
            <div className="qb-names">
              {status.available_quarterbacks?.slice(0, 8).map(qb => (
                <span key={qb} className="qb-tag">{qb}</span>
              ))}
              {status.available_quarterbacks?.length > 8 && 
                <span className="more">+{status.available_quarterbacks.length - 8} more</span>
              }
            </div>
          </div>
        </div>
      ) : (
        <div className="status-details">
          <div className="status-indicator">
            <span className="indicator red"></span>
            <span>Disconnected</span>
          </div>
          {status.error && <div className="error-msg">{status.error}</div>}
        </div>
      )}
    </div>
  );
};

// Query Input Component
const QueryInput = ({ onSubmit, isLoading, value, onChange }) => {
  const handleSubmit = (e) => {
    e.preventDefault();
    if (value.trim() && !isLoading) {
      onSubmit(value.trim());
    }
  };

  return (
    <form onSubmit={handleSubmit} className="query-form">
      <div className="input-group">
        <input
          type="text"
          value={value}
          onChange={(e) => onChange(e.target.value)}
          placeholder="Ask a question about 2024 NFL statistics..."
          className="query-input"
          disabled={isLoading}
        />
        <button 
          type="submit" 
          disabled={!value.trim() || isLoading}
          className="query-btn"
        >
          {isLoading ? 'üîÑ' : 'üîç'} Query
        </button>
      </div>
    </form>
  );
};

// Example Queries Component
const ExampleQueries = ({ onSelectExample, examples, isLoading }) => {
  return (
    <div className="examples-section">
      <h3>üí° Example Queries</h3>
      <div className="examples-grid">
        {examples.map((example, index) => (
          <button
            key={index}
            onClick={() => onSelectExample(example)}
            className="example-btn"
            disabled={isLoading}
          >
            {example}
          </button>
        ))}
      </div>
    </div>
  );
};

// Results Table Component
const ResultsTable = ({ data, columns, timing, rowsReturned }) => {
  if (!data || data.length === 0) {
    return (
      <div className="no-results">
        <h3>No Results Found</h3>
        <p>Try adjusting your query parameters or check your spelling.</p>
      </div>
    );
  }

  const downloadCSV = () => {
    const headers = columns.join(',');
    const rows = data.map(row => 
      columns.map(col => {
        const value = row[col];
        return typeof value === 'string' && value.includes(',') 
          ? `"${value}"` 
          : value;
      }).join(',')
    );
    const csv = [headers, ...rows].join('\n');

    const blob = new Blob([csv], { type: 'text/csv' });
    const url = window.URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = 'nfl_query_results.csv';
    a.click();
    window.URL.revokeObjectURL(url);
  };

  return (
    <div className="results-section">
      <div className="results-header">
        <h3>üìà Results</h3>
        <div className="results-actions">
          <div className="timing-info">
            <span>‚è±Ô∏è LLM: {timing.llm_time?.toFixed(2)}s</span>
            <span>üóÑÔ∏è DB: {timing.db_time?.toFixed(3)}s</span>
            <span>üìä {rowsReturned} rows</span>
          </div>
          <button onClick={downloadCSV} className="download-btn">
            üì• Download CSV
          </button>
        </div>
      </div>

      <div className="table-container">
        <table className="results-table">
          <thead>
            <tr>
              {columns.map(col => (
                <th key={col}>{col.replace(/_/g, ' ').toUpperCase()}</th>
              ))}
            </tr>
          </thead>
          <tbody>
            {data.map((row, index) => (
              <tr key={index}>
                {columns.map(col => (
                  <td key={col}>
                    {typeof row[col] === 'number' ? 
                      Number(row[col]).toLocaleString() : 
                      row[col]
                    }
                  </td>
                ))}
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

// SQL Query Display Component  
const SqlDisplay = ({ query, isVisible, onToggle }) => {
  if (!query) return null;

  return (
    <div className="sql-section">
      <button onClick={onToggle} className="sql-toggle">
        üîç {isVisible ? 'Hide' : 'Show'} Generated SQL
      </button>
      {isVisible && (
        <pre className="sql-code">
          <code>{query}</code>
        </pre>
      )}
    </div>
  );
};

// Main App Component
const App = () => {
  const [query, setQuery] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [results, setResults] = useState(null);
  const [error, setError] = useState(null);
  const [dbStatus, setDbStatus] = useState({ connected: false });
  const [examples, setExamples] = useState([]);
  const [showSql, setShowSql] = useState(false);

  // Load initial data
  useEffect(() => {
    loadDbStatus();
    loadExamples();
  }, []);

  const loadDbStatus = async () => {
    try {
      const status = await apiCall('/status');
      setDbStatus(status);
    } catch (err) {
      setDbStatus({ connected: false, error: err.message });
    }
  };

  const loadExamples = async () => {
    try {
      const response = await apiCall('/examples');
      setExamples(response.examples || []);
    } catch (err) {
      console.error('Failed to load examples:', err);
      // Fallback examples
      setExamples([
        "Top 5 QBs by passing yards",
        "Jared Goff completion percentage",
        "Third down conversion leaders",
        "Red zone touchdown percentage"
      ]);
    }
  };

  const executeQuery = async (queryText) => {
    if (!dbStatus.connected) {
      setError("Database is not connected. Please check the status.");
      return;
    }

    setIsLoading(true);
    setError(null);
    setResults(null);

    try {
      const response = await apiCall('/query', {
        method: 'POST',
        body: JSON.stringify({
          question: queryText,
          include_sql: true
        })
      });

      if (response.success) {
        setResults(response);
      } else {
        setError(response.error || 'Query failed');
      }
    } catch (err) {
      setError(`Request failed: ${err.message}`);
    } finally {
      setIsLoading(false);
    }
  };

  const handleExampleClick = (exampleQuery) => {
    setQuery(exampleQuery);
    executeQuery(exampleQuery);
  };

  return (
    <div className="App">
      <header className="app-header">
        <div className="hero-section">
          <h1>üèà NFL Query Assistant</h1>
          <p>Ask questions about 2024 NFL statistics in plain English!</p>
        </div>
      </header>

      <main className="app-main">
        <div className="layout">
          <div className="sidebar">
            <DatabaseStatus 
              status={dbStatus} 
              onRefresh={loadDbStatus} 
            />

            <ExampleQueries 
              examples={examples}
              onSelectExample={handleExampleClick}
              isLoading={isLoading}
            />
          </div>

          <div className="main-content">
            <QueryInput 
              onSubmit={executeQuery}
              isLoading={isLoading}
              value={query}
              onChange={setQuery}
            />

            {error && (
              <div className="error-message">
                <h3>‚ùå Error</h3>
                <p>{error}</p>
              </div>
            )}

            {isLoading && (
              <div className="loading-message">
                <div className="spinner"></div>
                <p>ü§ñ Processing your query...</p>
              </div>
            )}

            {results && results.success && (
              <>
                <ResultsTable 
                  data={results.data}
                  columns={results.columns}
                  timing={results.timing}
                  rowsReturned={results.rows_returned}
                />

                <SqlDisplay 
                  query={results.sql_query}
                  isVisible={showSql}
                  onToggle={() => setShowSql(!showSql)}
                />
              </>
            )}
          </div>
        </div>
      </main>

      <footer className="app-footer">
        <p>Powered by FastAPI + React ‚Ä¢ 2024 NFL Data</p>
      </footer>
    </div>
  );
};

export default App;
